mapper.delete:
/** 특정 게시물 삭제하기 */
DELETE
  FROM board
 WHERE writer = #{writer} AND bno = #{bno}

/** 관리자용 게시물 삭제(글번호만 가지고 -> 권한체크) */
    DELETE
      FROM board
     WHERE bno = #{bno}

mapper.insert:
/** 게시물 등록하기 */
INSERT INTO board(title, content , writer)
VALUES (#{title} , #{content}, #{writer})

mapper.update:
/** 게시물 업데이트 */
UPDATE board
   SET   title   = #{title}
       , content = #{content}
       , up_date = now()
 WHERE writer = #{writer} AND bno = #{bno}

/** 조회수 증가 */
UPDATE board
   SET view_cnt = view_cnt + 1
 WHERE bno = #{bno}

/** 댓글 수 업데이트 */
UPDATE board
   SET comment_cnt = comment_cnt + #{cnt}
 WHERE bno = #{bno}

mapper.sql:
/** 공통부분 분리하기 */
SELECT ROW_NUMBER() OVER(ORDER BY bno) AS rownum, bno , title , content , writer , view_cnt , comment_cnt , reg_date , up_date
  FROM board

mapper.select:
/** 게시물 페이징 */
SELECT ROW_NUMBER() OVER(ORDER BY bno) AS rownum, bno , title , content , writer , view_cnt , comment_cnt , reg_date , up_date
  FROM board
 ORDER BY reg_date DESC , bno DESC
LIMIT #{offset} , #{pageSize}

/** 총 게시물 개수 가져오기 */
SELECT count(*)
  FROM board
