mapper.select:
SELECT id, email
  FROM user_info
 WHERE email = #{email}

mapper.update:
UPDATE user_info
   SET  id         = #{id}
       ,pwd        = #{password}
       ,nickname   = #{nickName}
       ,email      = #{email}
       ,type       = #{type}
       ,status     = #{status}
       ,profile    = #{profile}
       ,role       = #{role}
       ,provider   = #{provider}
       ,providerId = #{providerId}
       ,createDate = #{createDate}
 WHERE  id = #{id}
