mapper.select:
/** 코멘트 삭제시 작성자명과 패스워드가 일치 하는지 */
SELECT EXISTS(
                SELECT writer , password
                  FROM SUGGESTION
                 WHERE writer = #{writer} and password = #{password}
)

mapper.delete:
DELETE
FROM suggestion
WHERE true
  AND writer = #{writer}
  AND sno = #{sno}
  AND password = #{password}

mapper.update:
UPDATE  suggestion
SET   content   = #{content}
  , writer = #{writer}
  , up_date   = now()
WHERE true
  AND writer = #{writer}
  AND sno = #{sno}
  AND password = #{password}
